select.if:
SELECT * FROM (SELECT ROWNUM RN, A.*
	FROM ( SELECT * FROM BOOK WHERE BTITLE LIKE '%'||UPPER(#{schWord})||'%' order by btitle) A)
		WHERE RN BETWEEN #{startRow} AND #{endRow}

SELECT * FROM (SELECT ROWNUM RN, A.*
	FROM ( SELECT * FROM BOOK WHERE bwriter LIKE '%'||UPPER(#{schWord})||'%' order by btitle) A)
		WHERE RN BETWEEN #{startRow} AND #{endRow}
